class ROM
{
    /**
     * Declare version of ROM Class
     * */
    public const VERSION = '1.0.2';

    /**
     * Framework configuration directory [default php runtime registries path]
     *
     * @var string
     */
    private string $defaultMemoryDir = PHP_RUNTIME_REGISTRIES_PATH;


    /**
     * ROM constructor.
     */
    public function __construct()
    {
        /*
         * Check read permission of parent directory of Framework configuration directory [default php runtime registries path]
         * throw read error message when not permitted
         * */
        if (file_exists(realpath(dirname($this->defaultMemoryDir)))) {
            if (!is_writable(realpath(dirname($this->defaultMemoryDir)))) {
                trigger_error(realpath(dirname($this->defaultMemoryDir)).' is not writeable.');
            }
        }

        /*
         * Check read permission of Framework configuration directory [default php runtime registries path]
         * throw read error message when not permitted
         * */
        if (file_exists($this->defaultMemoryDir)) {
            if (!is_readable($this->defaultMemoryDir)) {
                trigger_error($this->defaultMemoryDir.' is not readable.');
            }
        }

        /*
         * Check Framework configuration file existent,
         * check read permission of Framework configuration file
         * throw read error message when not permitted
         * */
        if (file_exists(Framework::configFile)) {
            if (!is_readable(Framework::configFile)) {
                trigger_error(Framework::configFile.' is not readable.');
            }
        }

    }//end __construct()


    /**
     * @param string $dir
     */
    /*
