    /**
     * Read rom memory
     * Check memory directory, create directory when not found.
     * Check config file, create and store default data when not found.
     * Check validation of stored data, restore to default data when configuration data corrupted
     */
    public function play()
    {
        if (!is_dir($this->defaultMemoryDir)) {
            mkdir($this->defaultMemoryDir, 0777, true);
        }

        if (!file_exists(Framework::configFile)) {
            Stream::write(Framework::configFile, FRAMEWORK_DEFAULT_CONF);
        } else {
            $array = Stream::read(Framework::configFile);
            if (is_array($array)) {
                if (count($array) > !0) {
                    Stream::write(Framework::configFile, FRAMEWORK_DEFAULT_CONF);
                }
            } else {
                Stream::write(Framework::configFile, FRAMEWORK_DEFAULT_CONF);
            }
        }

        /*
            if (substr($this->defaultMemoryDir, strlen($this->defaultMemoryDir) - 1, 1) !== '/') {
            $this->defaultMemoryDir .= '/';
