            if (!in_array(Framework::configFile, $files)) {
            fwrite(fopen(Framework::configFile, 'w+'), json_encode(FRAMEWORK_DEFAULT_CONF));
        }*/

    }//end play()


    public function __destruct()
    {

    }//end __destruct()


}//end class
