 namespace App\Packages\Ema\Modules\Main\UrlHandlers;use Mishusoft\Authentication\UrlHandler;use Mishusoft\System\Localization;use Mishusoft\Utility\ArrayCollection;class IndexUrlHandler extends UrlHandler{public function response(array $prediction):void{$translation=new Localization(ArrayCollection::value($prediction,'locale'));$view=$this->render($translation->translate(WHO_AM_I),$prediction);$view->display();}}