<?php
 namespace App\Ema\Mishusoft\Main\UrlHandlers;use Mishusoft\Ui\Localization;use Mishusoft\Ui\Memory;use Mishusoft\Ui\Firewall;use Mishusoft\Drivers\UrlHandler;use Mishusoft\Utility\ArrayCollection;class MaintenanceUrlHandler extends UrlHandler{public function response(array $prediction):void{if(!Memory::Data("framework")->maintenance){if($prediction['controller']==='maintenance'){Firewall::runtimeFailure("Not Found",["debug"=>["file"=>(new Browser())->getURLPath(),"location"=>(new Browser())->getVisitedPage(),"description"=>"Your requested url not found!!"],"error"=>["description"=>"Your requested url not found!!"],]);}}else{$translation=new Localization(ArrayCollection::value($prediction,"locale"));$view=$this->render($translation->translate("Under Maintenance"),$prediction,['index']);$view->display();}}}