 namespace App\Ema\Mishusoft\Main\UrlHandlers;use Mishusoft\Ui\Localization;use Mishusoft\Authentication\UrlHandler;use Mishusoft\Utility\ArrayCollection;class MonitorUrlHandler extends UrlHandler{public function response(array $prediction):void{$translation=new Localization(ArrayCollection::value($prediction,'locale'));$view=$this->render($translation->translate('Monitor'),$prediction);$view->display();}}