<?php
 namespace App\Widgets;use Mishusoft\Drivers\Widget;use App\Widgets\Models\menuModelWidget;class MenuWidget extends Widget{private menuModelWidget $menu;public function __construct(){parent::__construct();$this->menu=$this->loadModel('menu');}public function getMenu(string $menu,string $view,string $format='phtml',$inverse=null,$siteInfo=null):bool|string{return $this->render($menu,$view,['name'=>$menu,'menu'=>$this->menu->getSelectedMenu($view),'inverse'=>$inverse,'siteInfo'=>$this->menu->getSiteInfo(),],$format);}public function getConfig(string $menu):array{$menus['top']=['position'=>'header','show'=>'all','hide'=>['Login','Register','Password recovery','default_home','Payment','Sitemaps','sitemapHtml','Tracker',],];$menus['header']=['position'=>'header','show'=>'all','hide'=>['Login','Register','Password recovery','default_home','My home','Payment','Profile','Sitemaps','sitemapHtml','Tracker',],];$menus['left']=['position'=>'left','show'=>'all','hide'=>['Login','Register','Password recovery','My home','Payment','Sitemaps','sitemapHtml','Tracker',],];$menus['right']=['position'=>'right','show'=>'all','hide'=>['Login','Register','Password recovery','Payment','Sitemaps','sitemapHtml','Tracker',],];$menus['footer']=['position'=>'footer','show'=>'all','hide'=>['Login','Register','Password recovery','Payment','Sitemaps','sitemapHtml','Tracker',],];return $menus[$menu];}}